# Documentation Cleanup Script\n# Remove 13 redundant files after consolidation\n\nWrite-Host "`nDocumentation Cleanup Started...`n\" -ForegroundColor Green\n\n$docsPath = \"docs\"\n$files = @(\n    \"MIDDLEWARE_AUDIT.md\",\n    \"MIDDLEWARE_FINAL_REPORT.md\",\n    \"MIDDLEWARE_FLOW.md\",\n    \"MIDDLEWARE_INDEX.md\",\n    \"MIDDLEWARE_OPTIMIZATION.md\",\n    \"PROJECT_STRUCTURE.md\",\n    \"HEALTH_CHECK.md\",\n    \"TESTING_SOCKET_IO.md\",\n    \"CLEANUP_SUMMARY.md\"\n)\n\nforeach ($file in $files) {\n    $path = Join-Path $docsPath $file\n    if (Test-Path $path) {\n        Remove-Item $path -Force\n        Write-Host \"Deleted: $file\" -ForegroundColor Red\n    }\n}\n\nWrite-Host \"`nRemaining documentation files:\" -ForegroundColor Green\nGet-ChildItem $docsPath -Name -Filter \"*.md\" | Sort-Object\n\nWrite-Host \"`nCleanup complete!\" -ForegroundColor Green\n"}}]
</invoke>